import {useAuth0} from '@auth0/auth0-react';

const LoginButton = () => {
    const {loginWithRedirect,isAuthenticated} = useAuth0();
    const backgroundImageUrl = 'https://www.peoplegrove.com/wp-content/uploads/2022/07/8G2tiC2F-alumni-mentoring-00-header.png';
    const styles = {
        container: {
            background: `url(${backgroundImageUrl}) center/cover no-repeat fixed`, // Updated background properties
          height: '100vh', // Set the height to 100vh (100% of the viewport height)
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'left',
          justifyContent: 'left',
          color: 'black',
        },
        button: {
          padding: '10px 20px',
          backgroundColor: 'blue',
          color: 'white',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        },
      };
    return (
        !isAuthenticated && (
            <div style={styles.container}>
            <button style={styles.button} onClick={() => loginWithRedirect()}>
                Log In
            </button>
            </div>
        )
    )
}

export default LoginButton;
