// import React from 'react';

// function ProfilePage() {
//   return (
//     <div>
//       <h2>Profile Page</h2>
//       {/* Add your profile information and content here */}
//     </div>
//   );
// }

// export default ProfilePage;

import React from 'react';
import { Container, Grid, Paper, Typography, Avatar, Button } from '@mui/material';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    padding: theme.spacing(3),
  },
  paper: {
    padding: theme.spacing(2),
  },
  avatar: {
    width: 150,
    height: 150,
  },
}));

const ProfilePage = () => {
  const classes = useStyles();

  return (
    <Container className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={4}>
          <Paper className={classes.paper}>
            <Avatar className={classes.avatar} src="your-profile-image-url.jpg" />
            <Typography variant="h5" component="div">
              John Doe
            </Typography>
            <Typography color="textSecondary">
              Student
            </Typography>
            <Button variant="contained" color="primary">
              Connect
            </Button>
          </Paper>
        </Grid>
        <Grid item xs={8}>
          <Paper className={classes.paper}>
            <Typography variant="h5" component="div">
              About Me
            </Typography>
            <Typography>
              I'm a passionate student looking to connect with alumni for mentorship opportunities.
            </Typography>
            <Typography variant="h5" component="div">
              Skills
            </Typography>
            <Typography>
              - Web Development
              - JavaScript
              - React
              - HTML/CSS
              - ...
            </Typography>
            <Typography variant="h5" component="div">
              Contact Information
            </Typography>
            <Typography>
              Email: john.doe@example.com
            </Typography>
            <Typography>
              LinkedIn: <a href="https://www.linkedin.com/in/johndoe">https://www.linkedin.com/in/johndoe</a>
            </Typography>
          </Paper>
        </Grid>
      </Grid>
    </Container>
  );
};

export default ProfilePage;
