import React from 'react';

const MyComponent = () => {
  // Define the background image URL
  const backgroundImageUrl = 'https://www.peoplegrove.com/wp-content/uploads/2022/07/8G2tiC2F-alumni-mentoring-00-header.png';

  // Define the styles for the component
  const styles = {
    container: {
      background: `url(${backgroundImageUrl}) center/cover no-repeat fixed`, // Updated background properties
      height: '100vh', // Set the height to 100vh (100% of the viewport height)
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'left',
      justifyContent: 'left',
      color: 'black',
    },
    button: {
      padding: '10px 20px',
      backgroundColor: 'blue',
      color: 'white',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
    },
  };

  return (
    <div style={styles.container}>
    <div align = "left">
      <h1>Student Alumni Mentorship Portal</h1>
      <button style={styles.button}>Click Me</button>
    </div>
    </div>
  );
};

export default MyComponent;
